import React from "react";
import { useFormik } from "formik";

const YoutubeForm = () => {
  const formik = useFormik({
    initialValues: {
      name: "",
      email: "",
      channel: "",
    },
    onSubmit: (values) => console.log(values),
    validate: (values) => {
      // values.name, values.email, values.channel
      // errors.name, errors.email, errors.channel
      // Eg: errors.name = 'Name is required';
      const errors = {};
      if (!values.name) {
        return (errors.name = "Name Required");
      }

      if (!values.email) {
        return (errors.email = "Email Required");
      } else if (
        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2-4}$/i.test(values.email) // regex algo to 
      )
        if (!values.channel) {
          return (errors.channel = "Channel Required");
        }

      return errors;
    },
  });

  console.log(formik);
  // console.log(formik.values);
  return (
    <>
      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="name"
          name="name"
          onChange={formik.handleChange}
          value={formik.values.name}
        />

        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          onChange={formik.handleChange}
          value={formik.values.email}
        />

        <label htmlFor="channel">Channel</label>
        <input
          type="text"
          id="channel"
          name="channel"
          onChange={formik.handleChange}
          value={formik.values.channel}
        />

        <button type="submit">Submit</button>
        <button type="reset" onClick={formik.handleReset}>
          Reset
        </button>
        <hr />
        <br />
        <code>
          name: {formik.values.name} <br />
          email: {formik.values.email} <br />
          channel: {formik.values.channel}
        </code>
      </form>
    </>
  );
};
export default YoutubeForm;
